import React, { Component } from "react";
import { Redirect } from "react-router-dom";
import "./home.css";
import { PostData } from "../postdata";

class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      redirect: false,
      chatbox: false,
      profile: false,
      userdetails: [],
      fulldetails: [],
      file: "",
      imageUrl: null,
      SuccessMsg: false,
      userchat: [],
      urls: [],
      activestatus: false,
      userchatList: [],
      test: [],
      text: ""
    };
  }

  componentWillMount() {
    if (sessionStorage.getItem("formData")) {
      console.log("call user feed");
    } else {
      this.setState({ redirect: true });
    }
  }

  logout = () => {
    sessionStorage.setItem("formData", "");
    sessionStorage.clear();
    this.setState({ redirect: true });
  };

  onchat = () => {
    this.setState({ chatbox: true });
    const name = this.props.location.state[0].name;
    var userData = new FormData();
    userData.append("name", name);
    PostData(userData, "/home").then(result => {
      let responseJson = result;

      var data12 = responseJson.data;
      const data = [...this.state.userdetails, data12];
      if (responseJson.status === "success") {
        this.setState({ userdetails: data });

        const counters = this.state.userdetails.map(c => {
          c.map(u => {
            //console.log(u.image);
          });
        });
      }
      //console.log(this.state.userdetails);
    });
  };

  profile = () => {
    this.setState({ profile: true });
  };

  onChange = event => {
    /* var file = event.target.files;
    console.log(file); */

    const file = this.refs.uploadImg.files[0];
    console.log(file);

    const reader = new FileReader();
    console.log(this.state.file);
    reader.onloadend = () => {
      this.setState({
        imageUrl: reader.result
      });
    };
    if (file) {
      const b = file.name;
      console.log(b);
      this.setState({ file: b });
      reader.readAsDataURL(file);
      this.setState({
        imageUrl: reader.result
      });
      console.log(this.state.imageUrl);
    } else {
      this.setState({
        imageUrl: ""
      });
    }

    this.setState({ [event.target.name]: event.target.value });

    console.log(this.state);
  };

  onSave = () => {
    console.log(this.state);
    /*  var a = this.state.imageUrl;
    const data8 = [...this.state.urls, a];
    this.setState({ urls: data8 });

    console.log(data8); */
    var saveData = new FormData();
    saveData.append("image_url", this.state.imageUrl);
    saveData.append("phone", this.state.phone);
    saveData.append("address", this.state.address);
    saveData.append("email", this.props.location.state[0].email);
    PostData(saveData, "/save").then(result => {
      let resultJSON = result;
      var data12 = resultJSON.data;
      const data = [...this.state.fulldetails, data12];
      console.log(resultJSON);
      if (resultJSON.status === "success") {
        console.log(this.state.SuccessMsg);
        this.setState({ fulldetails: data });
        this.setState({ SuccessMsg: true });
        console.log(this.state.SuccessMsg);
      }
    });
  };

  onUserSelect = user => {
    console.log(user);
    // const chats = [...this.state.userchat, user];
    this.state.activestatus = true;
    this.setState({
      userchat: user,
      activestatus: { [user.id]: this.state.activestatus }
    });
  };
  changeStatus = (user, key) => {
    //console.log("key", key);
    let initial;
    /*   if (key === 0) {
      initial = "active";
    } else { */
    initial = this.state.activestatus[user.id] === true ? "active" : "";
    /* } */
    return initial;
  };

  handleChange = event => {
    this.setState({ text: event.target.value });
  };

  OnstartChat = (u, event) => {
    // console.log("u", u);
    //console.log(event);
    var a = event.target.value;
    //this.setState({ userchatList: this.state.pricelog.concat(a) });
    //const data = [...this.state.userchatList, a];

    if (event.key === "Enter") {
      event.preventDefault();
      console.log(event.target.value);

      this.setState({
        // userchatList: data,
        test: { [u.id]: event.target.value }

        //[event.target.value]: ""
      });
      event.target.value = "";
    }

    /*  const a1 = [...this.state.userchatList, this.state.test];
    console.log("a", a1); */
    this.setState({
      userchatList: this.state.test
    });
    console.log(this.state.userchatList);
    //this.setState({ test: newFile });
    //console.log(this.state.test[u.id]);

    //console.log("text", this.state.text);
  };

  componentDidUpdate() {
    console.log("test", this.state.test);
    const userchatList = [...this.state.userchatList, this.state.test];
    {
      this.state.userchatList.map((t, key) => console.log("t", t, key));
    }
    console.log(userchatList);
  }

  render() {
    // console.log(this.state.userchat.id);
    console.log(this.state.test);
    
    //console.log(this.props.location.state[0].name);
    //console.log(this.state.userchat.length);

    if (this.state.redirect) {
      return <Redirect to={"/signin"} />;
    }
    return (
      <header>
        <button
          id="logout"
          type="button"
          className="btn btn-warning btn-sm"
          onClick={this.logout}
        >
          Logout
        </button>

        <div className="clearfix" />

        {this.state.SuccessMsg || this.state.chatbox ? (
          <div>
            {this.state.chatbox ? (
              <div className="container-fluid h-100">
                <div className="row justify-content-center h-100">
                  <div className="col-md-4 col-xl-3 chat">
                    <div className="card mb-sm-3 mb-md-0 contacts_c ard">
                      <div className="card-header">
                        <div className="input-group">
                          <input
                            type="text"
                            placeholder="Search..."
                            name=""
                            className="form-control search"
                          />
                          <div className="input-group-prepend">
                            <span className="input-group-text search_btn">
                              <i className="fas fa-search" />
                            </span>
                          </div>
                        </div>
                      </div>
                      <div className="card-body contacts_body">
                        <ul className="contacts">
                          {this.state.userdetails.map((user, key) =>
                            user.map((u, key) => (
                              <li
                                className={this.changeStatus(u, key)}
                                key={key}
                                onClick={() => this.onUserSelect(u)}
                              >
                                <div className="d-flex bd-highlight">
                                  <div className="img_cont">
                                    <img
                                      src={u.image}
                                      className="rounded-circle user_img"
                                    />
                                    <span className="online_icon" />
                                  </div>
                                  <div className="user_info">
                                    <span>{u.name}</span>
                                    <p>Maryam is online</p>
                                  </div>
                                </div>
                              </li>
                            ))
                          )}
                        </ul>
                      </div>
                      <div className="card-footer" />
                    </div>
                  </div>
                  <div className="col-md-8 col-xl-6 chat">
                    <div>
                      {this.state.userchat.id ? (
                        <div className="card">
                          <div className="card-header msg_head">
                            <div className="d-flex bd-highlight">
                              <div className="img_cont">
                                <img
                                  src={this.state.userchat.image}
                                  className="rounded-circle user_img"
                                />
                                <span className="online_icon" />
                              </div>
                              <div className="user_info">
                                <span>
                                  Chat with {this.state.userchat.name}
                                </span>
                                <p>1767 Messages</p>
                              </div>
                              <div className="video_cam">
                                <span>
                                  <i className="fas fa-video" />
                                </span>
                                <span>
                                  <i className="fas fa-phone" />
                                </span>
                              </div>
                            </div>
                            <span id="action_menu_btn">
                              <i className="fas fa-ellipsis-v" />
                            </span>
                            <div className="action_menu">
                              <ul>
                                <li>
                                  <i className="fas fa-user-circle" /> View
                                  profile
                                </li>
                                <li>
                                  <i className="fas fa-users" /> Add to close
                                  friends
                                </li>
                                <li>
                                  <i className="fas fa-plus" /> Add to group
                                </li>
                                <li>
                                  <i className="fas fa-ban" /> Block
                                </li>
                              </ul>
                            </div>
                          </div>
                          <div className="card-body msg_card_body">
                            {/* <div className="d-flex justify-content-start mb-4">
                              <div className="img_cont_msg">
                                <img
                                  src="https://2.bp.blogspot.com/-8ytYF7cfPkQ/WkPe1-rtrcI/AAAAAAAAGqU/FGfTDVgkcIwmOTtjLka51vineFBExJuSACLcBGAs/s320/31.jpg"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                              <div className="msg_cotainer">
                                Hi, how are you samim?
                                <span className="msg_time">8:40 AM, Today</span>
                              </div>
                            </div> */}
                            {this.state.userchatList.map((u1, key) => (
                              <div
                                key={key}
                                className="d-flex justify-content-end mb-4"
                              >
                                <div className="msg_cotainer_send">
                                  {u1}
                                  <span className="msg_time_send">
                                    8:55 AM, Today
                                  </span>
                                </div>
                                <div className="img_cont_msg">
                                  <img
                                    src={this.state.userchat.image}
                                    className="rounded-circle user_img_msg"
                                  />
                                </div>
                              </div>
                            ))}

                            {/* <div className="d-flex justify-content-start mb-4">
                              <div className="img_cont_msg">
                                <img
                                  src="https://devilsworkshop.org/files/2013/01/enlarged-facebook-profile-picture.jpg"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                              <div className="msg_cotainer">
                                I am good too, thank you for your chat template
                                <span className="msg_time">9:00 AM, Today</span>
                              </div>
                            </div>
                            <div className="d-flex justify-content-end mb-4">
                              <div className="msg_cotainer_send">
                                You welcome Maryam
                                <span className="msg_time_send">
                                  9:05 AM, Today
                                </span>
                              </div>
                              <div className="img_cont_msg">
                                <img
                                  src="data:image/jpeg;base64,"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                            </div>
                            <div className="d-flex justify-content-start mb-4">
                              <div className="img_cont_msg">
                                <img
                                  src="https://devilsworkshop.org/files/2013/01/enlarged-facebook-profile-picture.jpg"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                              <div className="msg_cotainer">
                                I am looking for your next templates
                                <span className="msg_time">9:07 AM, Today</span>
                              </div>
                            </div>
                            <div className="d-flex justify-content-end mb-4">
                              <div className="msg_cotainer_send">
                                Ok, thank you have a good day
                                <span className="msg_time_send">
                                  9:10 AM, Today
                                </span>
                              </div>
                              <div className="img_cont_msg">
                                <img
                                  src="data:image/jpeg;base64,"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                            </div>
                            <div className="d-flex justify-content-start mb-4">
                              <div className="img_cont_msg">
                                <img
                                  src="https://devilsworkshop.org/files/2013/01/enlarged-facebook-profile-picture.jpg"
                                  className="rounded-circle user_img_msg"
                                />
                              </div>
                              <div className="msg_cotainer">
                                Bye, see you
                                <span className="msg_time">9:12 AM, Today</span>
                              </div>
                            </div> */}
                          </div>
                          <div className="card-footer">
                            <div className="input-group">
                              <div className="input-group-append">
                                <span className="input-group-text attach_btn">
                                  <i className="fas fa-paperclip" />
                                </span>
                              </div>
                              <textarea
                                name=""
                                className="form-control type_msg"
                                onKeyDown={event =>
                                  this.OnstartChat(this.state.userchat, event)
                                }
                                onChange={this.handleChange}
                                placeholder="Type your message..."
                              />
                              <div className="input-group-append">
                                <span className="input-group-text send_btn">
                                  <i className="fas fa-location-arrow" />
                                </span>
                              </div>
                            </div>
                          </div>
                        </div>
                      ) : (
                        <p className="colore">Start chatting here!!!</p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div>
                <svg
                  className="checkmark"
                  xmlns="http://www.w3.org/2000/svg"
                  viewBox="0 0 52 52"
                >
                  <circle
                    className="checkmark__circle"
                    cx="26"
                    cy="26"
                    r="25"
                    fill="none"
                  />
                  <path
                    className="checkmark__check"
                    fill="none"
                    d="M14.1 27.2l7.1 7.2 16.7-16.8"
                  />
                </svg>
                <div className="success_msg">
                  Your Profile has been Updated Successfully
                </div>
                <button
                  type="button"
                  id="chat"
                  className="btn btn-default btn-lg"
                  onClick={this.onchat}
                >
                  Wanna Chat?
                </button>
              </div>
            )}
          </div>
        ) : (
          <div className="custm_top">
            <div className="title">
              <span>welcome {this.props.location.state[0].name}!!</span>
            </div>
            {this.state.profile ? (
              <div className="container">
                <div className="row">
                  <div className="full-width bg-transparent">
                    <div className="col-lg-6 col-lg-offset-3 col-md-7 col-md-offset-3 col-xs-4">
                      <div className="full-width">
                        <div className="custom-form">
                          <div className="img-section">
                            {this.state.imageUrl ? (
                              <img
                                src={this.state.imageUrl}
                                className="imgCircle"
                                alt="Profile picture"
                              />
                            ) : (
                              <img
                                src="http://lorempixel.com/200/200/nature/"
                                className="imgCircle img-responsive"
                                alt="Profile picture"
                              />
                            )}

                            <div className="">
                              <h4 className="text-right">
                                <span className="glyphicon glyphicon-edit" />{" "}
                                Edit Profile
                              </h4>
                              <div className="image-upload">
                                <label htmlFor="file-input">
                                  <i className="ion-android-camera icon upload-button" />
                                </label>
                                <input
                                  id="file-input"
                                  type="file"
                                  accept="image/*"
                                  ref="uploadImg"
                                  onChange={this.onChange}
                                />
                              </div>
                            </div>
                          </div>

                          <div className="col-lg-12 col-md-12">
                            <input
                              type="text"
                              className="form-control form-input"
                              value={this.props.location.state[0].name}
                              placeholder="Name"
                              disabled
                              id="name"
                            />
                            <span className="glyphicon glyphicon-user input-place" />
                          </div>
                          <div className="col-lg-12 col-md-12">
                            <input
                              type="text"
                              className="form-control form-input"
                              value={this.props.location.state[0].email}
                              placeholder="Email ID"
                              disabled
                              id="email"
                            />
                            <span className="glyphicon glyphicon-envelope input-place" />
                          </div>
                          <div className="col-lg-12 col-md-12">
                            <input
                              type="text"
                              className="form-control form-input"
                              placeholder="Phone Number"
                              name="phone"
                              onChange={this.onChange}
                              id="phone"
                            />
                            <span className="glyphicon glyphicon-earphone input-place" />
                          </div>
                          <div className="col-lg-12 col-md-12">
                            <input
                              type="text"
                              className="form-control form-input"
                              placeholder="Address"
                              name="address"
                              onChange={this.onChange}
                              id="place"
                            />
                            <span className="glyphicon glyphicon-map-marker input-place" />
                          </div>
                          <div className="col-lg-12 col-md-12 text-center">
                            <button
                              className="btn btn-info btn-lg custom-btn"
                              id="submit"
                              onClick={this.onSave}
                            >
                              Save
                            </button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ) : (
              <div>
                <button
                  id="profile"
                  type="button"
                  className="btn btn-primary btn-lg"
                  onClick={this.profile}
                >
                  Update Profile
                </button>{" "}
                <button
                  type="button"
                  id="chat"
                  className="btn btn-default btn-lg"
                  onClick={this.onchat}
                >
                  Wanna Chat?
                </button>
              </div>
            )}
          </div>
        )}

        {/*  <div className="overlay" />
        {  <video
          playsinline="playsinline"
          autoplay="autoplay"
          muted="muted"
          loop="loop"
        >
          <source
            src="https://storage.googleapis.com/coverr-main/mp4/Mt_Baker.mp4"
            type="video/mp4"
          />
        </video> } */}
      </header>
    );
  }
}

export default Home;
